import React,{ useState } from 'react';
import {  BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Page1 from './Pages/Page1';
import Page2 from './Pages/Page2';
import Page3 from './Pages/Page3';
import Page4 from './Pages/Page4';
import Page5 from './Pages/Page5';
import MemoryMatch from './Pages/MemoryMatch';
import PuzzleGame from './Pages/PuzzleGame';
import CipherCracker from './Pages/CipherCracker';
import TriviaQuiz from './Pages/TriviaQuiz';
import FinalGame from './Pages/FinalGame';


function App() 
  {
    const [stage, setStage] = useState(1); // 1 = Memory Match, 2 = Puzzle, 3 = Cipher, 4 = Trivia, 5 = Final Game

    const nextStage = () => {
      setStage(stage + 1);
    };
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Page1 />} />
        <Route path="/story" element={<Page2 />} />
        <Route path="/games" element={<Page3 />} />
          <div className="App">
           <h1>Love Game Series</h1>
            <div className="game-container">
              {stage === 1 && <MemoryMatch onComplete={nextStage} />}
              {stage === 2 && <PuzzleGame onComplete={nextStage} />}
              {stage === 3 && <CipherCracker onComplete={nextStage} />}
              {stage === 4 && <TriviaQuiz onComplete={nextStage} />}
              {stage === 5 && <FinalGame />}
            </div>
         </div>
        <Route path="/proposal" element={<Page4 />} />
        <Route path="/memories" element={<Page5 />} />
      </Routes>
    </Router>
  );
}

export default App;
