// src/Pages/Page4.js
import React, { useState } from 'react';
import Confetti from 'react-confetti';
import './Page4.css';

const Page4 = () => {
  const [clickedYes, setClickedYes] = useState(false);
  const [width, height] = [window.innerWidth, window.innerHeight];

  const handleYesClick = () => {
    setClickedYes(true);
  };

  const handleNoClick = (event) => {
    event.target.style.transform = `translateX(${Math.random() * 100 - 50}px) translateY(${Math.random() * 100 - 50}px)`;
  };

  return (
    <div className="page4-container">
      {clickedYes && <Confetti width={width} height={height} />}
      <h1 className="question">Will You Be My Valentine?</h1>
      <div className="buttons">
        <button className="no-btn" onClick={handleNoClick}>NO ‚ùå</button>
        <button className="yes-btn" onClick={handleYesClick}>YES ‚ù§Ô∏è</button>
      </div>
      {clickedYes && (
        <div className="surprise">
          <h2>"I Said Yes!"</h2>
          <div className="couple-anim">üíë</div>
          <audio autoPlay>
            <source src="/assets/love-message.mp3" type="audio/mp3" />
          </audio>
        </div>
      )}
    </div>
  );
};

export default Page4;
