import React, { useState, useEffect } from 'react';
import './PuzzleGame.css';

// Define 4 unique candy colors
const candyColors = [
  '#ff6666', '#66ccff', '#99cc66', '#ffcc33',
];

// Function to generate a random candy color from the 4 available candies
const generateRandomCandy = () => {
  return candyColors[Math.floor(Math.random() * candyColors.length)];
};

const PuzzleGame = ({ level }) => {
  const [gameBoard, setGameBoard] = useState([]);
  const [isGameWon, setIsGameWon] = useState(false);
  const [selectedCandy, setSelectedCandy] = useState(null);

  // Initialize the board with random candies (3x3 grid for 9 candies)
  const initializeBoard = () => {
    const rows = 3;  // 3 rows
    const cols = 3;  // 3 columns
    const board = [];

    for (let row = 0; row < rows; row++) {
      const rowArray = [];
      for (let col = 0; col < cols; col++) {
        rowArray.push(generateRandomCandy());
      }
      board.push(rowArray);
    }

    setGameBoard(board);
  };

  // Handle candy click (select or swap)
  const handleCandyClick = (row, col) => {
    if (!selectedCandy) {
      // Select the candy for the first click
      setSelectedCandy({ row, col });
    } else {
      const { row: prevRow, col: prevCol } = selectedCandy;
      if (
        (Math.abs(prevRow - row) === 1 && prevCol === col) || // Vertical swap
        (Math.abs(prevCol - col) === 1 && prevRow === row) // Horizontal swap
      ) {
        // Swap candies if they are adjacent
        let newBoard = [...gameBoard];
        const temp = newBoard[prevRow][prevCol];
        newBoard[prevRow][prevCol] = newBoard[row][col];
        newBoard[row][col] = temp;
        setGameBoard(newBoard);

        // After swapping, check for matches and drop new candies
        if (!checkMatches()) {
          // If no match, swap back
          newBoard = [...gameBoard];
          newBoard[prevRow][prevCol] = newBoard[row][col];
          newBoard[row][col] = temp;
          setGameBoard(newBoard);
        } else {
          // After matching, drop new candies
          dropCandies();
        }
      }
      // Reset the selection after swap
      setSelectedCandy(null);
    }
  };

  // Check for horizontal matches of 3
  const checkHorizontalMatches = () => {
    let newBoard = [...gameBoard];
    let matches = [];

    for (let row = 0; row < 3; row++) {
      for (let col = 0; col < 1; col++) {  // Only check up to col 1 for 3-item match
        if (
          newBoard[row][col] === newBoard[row][col + 1] &&
          newBoard[row][col] === newBoard[row][col + 2]
        ) {
          matches.push([row, col], [row, col + 1], [row, col + 2]);
        }
      }
    }
    return matches;
  };

  // Check for vertical matches of 3
  const checkVerticalMatches = () => {
    let newBoard = [...gameBoard];
    let matches = [];

    for (let col = 0; col < 3; col++) {
      for (let row = 0; row < 1; row++) {  // Only check up to row 1 for 3-item match
        if (
          newBoard[row][col] === newBoard[row + 1][col] &&
          newBoard[row][col] === newBoard[row + 2][col]
        ) {
          matches.push([row, col], [row + 1, col], [row + 2, col]);
        }
      }
    }
    return matches;
  };

  // Check for matches (both horizontal and vertical)
  const checkMatches = () => {
    let newBoard = [...gameBoard];
    const horizontalMatches = checkHorizontalMatches();
    const verticalMatches = checkVerticalMatches();

    const allMatches = [...horizontalMatches, ...verticalMatches];

    if (allMatches.length > 0) {
      // Mark matched candies as null (clearing them)
      allMatches.forEach(([row, col]) => {
        newBoard[row][col] = null;
      });
      setGameBoard(newBoard);
      return true;
    }
    return false;
  };

  // After matching candies, drop new ones to fill empty spots
  const dropCandies = () => {
    let newBoard = [...gameBoard];
    for (let col = 0; col < 3; col++) {
      // Fill empty spots by shifting candies down
      for (let row = 2; row >= 0; row--) {
        if (newBoard[row][col] === null) {
          for (let r = row - 1; r >= 0; r--) {
            if (newBoard[r][col] !== null) {
              newBoard[row][col] = newBoard[r][col];
              newBoard[r][col] = null;
              break;
            }
          }
        }
      }
    }

    // Fill the empty spots at the top with random candies
    for (let row = 0; row < 3; row++) {
      for (let col = 0; col < 3; col++) {
        if (newBoard[row][col] === null) {
          newBoard[row][col] = generateRandomCandy();
        }
      }
    }

    setGameBoard(newBoard);
  };

  // Initialize the board on first render
  useEffect(() => {
    initializeBoard();
  }, [level]);

  return (
    <div className="game-container">
      <h1>Level {level}</h1>

      <div className="game-board">
        {gameBoard.map((row, rowIndex) => (
          <div key={rowIndex} className="game-row">
            {row.map((cell, colIndex) => (
              <div
                key={colIndex}
                className="game-cell"
                style={{ backgroundColor: cell }}
                onClick={() => handleCandyClick(rowIndex, colIndex)}
              />
            ))}
          </div>
        ))}
      </div>

      {isGameWon && <div className="win-message">You Won this Level!</div>}
    </div>
  );
};

export default PuzzleGame;
