import React, { useEffect, useState } from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';

const GoogleMapComponent = () => {
  // Google Map container style
  const mapContainerStyle = {
    width: '100%',
    height: '450px',
  };

  // Center the map on a default location (you can change it to the first marker)
  const center = {
    lat: 28.6139,  // Default to Delhi
    lng: 77.2090,
  };

  // Locations to be marked
  const locations = [
    {
      label: 'F', // First meet
      title: 'Pune - First Meet',
      lat: 18.5204,
      lng: 73.8567,
    },
    {
      label: 'M', // Marriage
      title: 'Lucknow - Marriage',
      lat: 26.8467,
      lng: 80.9462,
    },
    {
      label: 'H', // Honeymoon
      title: 'Goa - Honeymoon',
      lat: 15.2993,
      lng: 73.9240,
    },
    {
      label: 'D', // First date
      title: 'Delhi - First Date',
      lat: 28.6139,
      lng: 77.2090,
    },
  ];

  return (
    <LoadScript googleMapsApiKey="YOUR_GOOGLE_MAPS_API_KEY">
      <GoogleMap
        mapContainerStyle={mapContainerStyle}
        center={center}
        zoom={6}
      >
        {/* Add markers for each location */}
        {locations.map((location, index) => (
          <Marker
            key={index}
            position={{ lat: location.lat, lng: location.lng }}
            label={location.label}
            title={location.title}
          />
        ))}
      </GoogleMap>
    </LoadScript>
  );
};

export default GoogleMapComponent;
