import React, { useState } from 'react';
import './Page3.css';

// A simple puzzle setup (add images or pieces in practice)
const puzzlePieces = ['â¤ï¸', 'ðŸ’', 'ðŸ’Œ', 'ðŸŒ¹','ðŸ˜','ðŸ’‹','ðŸŒ¹','ðŸŒ»','â˜˜ï¸'];

function PuzzleGame({ onComplete }) {
  const [pieces, setPieces] = useState(puzzlePieces);

  const shufflePieces = () => {
    const shuffled = [...pieces].sort(() => Math.random() - 0.5);
    setPieces(shuffled);
  };

  const checkPuzzle = () => {
    if (JSON.stringify(pieces) === JSON.stringify(puzzlePieces)) {
      onComplete();
    }
  };

  return (
    <div>
      <h2>Jigsaw Puzzle</h2>
      <div className="puzzle-board">
        {pieces.map((piece, index) => (
          <div key={index} className="puzzle-piece" onClick={() => shufflePieces()}>
            {piece}
          </div>
        ))}
      </div>
      <button onClick={checkPuzzle}>Check Puzzle</button>
    </div>
  );
}

export default PuzzleGame;
