import React, { useState } from 'react';
import './PuzzleGame.css'

// Function to generate a solvable 3x3 Sudoku puzzle
function generatePuzzle() {
  const validPuzzle = [
    [1, 0, 3],
    [0, 2, 0],
    [0, 0, 1],
  ];

  // A simple solvable Sudoku puzzle. You can expand or randomize the puzzle generation logic here.
  return validPuzzle;
}

const Puzzle = () => {
  const [showHowToPlay, setShowHowToPlay] = useState(false);
  const [gameStatus, setGameStatus] = useState(""); // 'win' or 'lose'
  const [grid, setGrid] = useState(generatePuzzle()); // Initialize with a valid puzzle

  // Toggle visibility of the How to Play instructions
  const toggleHowToPlay = () => {
    setShowHowToPlay(!showHowToPlay);
  };

  // Handle changes to the grid cells, but prevent editing pre-filled cells
  const handleChange = (e, row, col) => {
    if (grid[row][col] === 0) {
      const newGrid = grid.map((rowArray, rowIndex) =>
        rowIndex === row
          ? rowArray.map((value, colIndex) =>
              colIndex === col ? parseInt(e.target.value) : value
            )
          : rowArray
      );
      setGrid(newGrid);
    }
  };

  // Check if the current grid is a valid solved puzzle
  const checkSolution = () => {
    const isValid = checkRows() && checkColumns();
    if (isValid) {
      setGameStatus("win");
      setTimeout(() => alert("You win! Redirecting to a new game..."), 500);
      setTimeout(resetGame, 2000); // Reset after 2 seconds
    } else if (isGridFilled()) {
      setGameStatus("lose");
      setTimeout(() => alert("Try again! You can do it!"), 500);
    }
  };

  // Check if the grid is completely filled with numbers
  const isGridFilled = () => {
    return grid.every(row => row.every(cell => cell !== 0));
  };

  // Check if rows contain numbers 1 to 3 with no repetition
  const checkRows = () => {
    return grid.every(row => {
      const uniqueValues = new Set(row);
      return uniqueValues.size === row.length && !row.includes(0);
    });
  };

  // Check if columns contain numbers 1 to 3 with no repetition
  const checkColumns = () => {
    for (let col = 0; col < 3; col++) {
      const column = [grid[0][col], grid[1][col], grid[2][col]];
      const uniqueValues = new Set(column);
      if (uniqueValues.size !== column.length || column.includes(0)) {
        return false;
      }
    }
    return true;
  };

  // Reset the game grid to a new puzzle
  const resetGame = () => {
    setGameStatus(""); // Reset game status
    setGrid(generatePuzzle()); // Reset grid with a new valid puzzle
  };

  const renderGrid = () => {
    return grid.map((row, rowIndex) => (
      <div className="row" key={rowIndex}>
        {row.map((cell, colIndex) => (
          <input
            key={colIndex}
            type="number"
            min="1"
            max="3"
            value={cell === 0 ? '' : cell}
            onChange={(e) => handleChange(e, rowIndex, colIndex)}
            className={`cell ${cell !== 0 ? 'disabled' : ''}`}
            disabled={cell !== 0} // Disable editing for pre-filled cells
          />
        ))}
      </div>
    ));
  };

  return (
    <div className="puzzle-wrapper">
      <div className="left-side">
        <button className="how-to-play-btn" onClick={toggleHowToPlay}>
          {showHowToPlay ? 'Hide How to Play' : 'How to Play'}
        </button>

        {showHowToPlay && (
          <div className="how-to-play">
            <h2>How to Play:</h2>
            <p>
              The goal of Sudoku is to fill the grid with numbers such that:
            </p>
            <ul>
              <li>Each row contains the numbers 1 through 3 (without repeating).</li>
              <li>Each column contains the numbers 1 through 3 (without repeating).</li>
            </ul>
            <p>Fill in the blanks and try to solve the puzzle!</p>
          </div>
        )}
      </div>

      <div className="right-side">
        <h1 className="title">3x3 Sudoku</h1>
        <div className="puzzle-container">{renderGrid()}</div>
        <button className="check-button" onClick={checkSolution}>Check Solution</button>
        {gameStatus === "win" && <div className="win-message">You Win! Redirecting to a new game...</div>}
        {gameStatus === "lose" && <div className="lose-message">Try again! You can do it!</div>}
        <button className="try-again-button" onClick={resetGame}>Try Again</button>
      </div>
    </div>
  );
};

export default Puzzle;
