import React, { useState,useRef } from 'react';
import { Howl } from 'howler';
import './FinalGame.css';
import Confetti from 'react-confetti';
import { FaHeart } from 'react-icons/fa';
  
  function FinalGame() {
    
      const [musicPlaying, setMusicPlaying] = useState(false); // Start with music paused
      const [sound, setSound] = useState(null); // The sound will be created on user interaction
    
      const toggleMusic = () => {
        if (!sound) {
          // Initialize sound only after the user clicks (only once)
          const music = new Howl({
            src: [
              '/assets/Friends.mp3', // MP3 format
              '/assets/Friends.ogg', // OGG fallback format
            ],
            volume: 0.5,
            loop: true, // Optional: makes the music loop
            onplay: () => {
              console.log('Music started playing');
            },
            onloaderror: (id, error) => {
              console.error('Error loading the music file:', error);
            },
            onplayerror: (id, error) => {
              console.error('Error playing the music:', error);
            },
          });
          setSound(music); // Store the Howl instance
          music.play(); // Play the music after initialization
        } else {
          if (musicPlaying) {
            sound.stop(); // Stop the existing instance
          } else {
            sound.play(); // Play the existing instance
          }
        }
        setMusicPlaying(!musicPlaying); // Toggle play/pause state
      



    const [answer, setAnswer] = useState(null);
    const [confetti, setConfetti] = useState(false);
    const noButtonRef = useRef(null); // Reference for the No button
  
    // Handle the "Yes" and "No" responses
    const handleClick = (response) => {
      setAnswer(response);
      if (response) {
        setConfetti(true);
      }
    };
  
    // Handle the "No" button moving away
    const handleNoMouseMove = () => {
      if (noButtonRef.current) {
        const button = noButtonRef.current;
        const buttonRect = button.getBoundingClientRect();
  
        // Calculate a random small move between 5px and 15px
        const moveX = Math.random() * 305;
        const moveY = Math.random() * 305;
  
        // Move the button a few pixels in random directions
        button.style.transform = `translate(${moveX}px, ${moveY}px)`;
      }
    }};
  
    return (
      <div className="App">
        <audio autoPlay loop>
          <source src='asset/Friends.mp3' type="audio/mp3" />
          Your browser does not support the audio element.
        </audio>
  
        {confetti && <Confetti />}
  
        <div className="valentine-container">
          <h1 className="title">🌹 Will you be my Valentine? 🌹</h1>
  
          <div className={`heart-container ${answer !== null ? 'zoom-in' : ''}`}>
            <FaHeart className="heart" />
          </div>
  
          {answer === null && (
            <div className="buttons">
              <button onClick={() => handleClick(true)} className="yes-button">Yes 💖</button>
              <button
                ref={noButtonRef} 
                onClick={() => handleClick(false)} 
                className="no-button"
                onMouseMove={handleNoMouseMove}
              >  
                No 💔
              </button>
              <button className="music-toggle" onClick={toggleMusic}>
        {musicPlaying ? 'Mute' : 'Play'} Music
      </button>
            </div>
            
          )}
  
          {answer !== null && (
            <div className="response">
              {answer ? (
                <p className="response-text">Yay! 💖 You said YES! 💖</p>
              ) : (
                <p className="response-text">Whyyyyyyy!! 💔 Fir se choose kro...</p>
              )}
            </div>
          )}
        </div>
      </div>
    );
  }
  
 

export default FinalGame;
