import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Howl } from 'howler';
import './Page1.css';

const Page1 = () => {
  const [musicPlaying, setMusicPlaying] = useState(true);
  const [sound, setSound] = useState(null);

  useEffect(() => {
    // Create the sound instance when the component is mounted
    const music = new Howl({
      src: ['/assets/romantic-music.mp3'],  // Make sure the path is correct
      volume: 1.0,
      loop: true,  // Optional: makes the music loop
      onload: () => {
        // Music is ready to play after loading
        if (musicPlaying) {
          music.play(); // Start playing music automatically
        }
      },
    });

    setSound(music);

    return () => {
      music.stop();  // Stop music when the component unmounts
    };
  }, []);

  const toggleMusic = () => {
    if (sound) {
      if (musicPlaying) {
        sound.stop();
      } else {
        sound.play();
      }
      setMusicPlaying(!musicPlaying);
    }
  };

  return (
    <div className="page1-container">
      <div className="welcome-message">
        <h1 className="animated-text">Welcome, My Love, Adarsh ❤️</h1>
        <p className="animated-heartfelt-message">
          This is a journey made just for you, my love. Follow your heart, and let’s relive our story together.
        </p>
      </div>
      <div className="floating-hearts"></div>
      <button className="music-toggle" onClick={toggleMusic}>
        {musicPlaying ? 'Mute' : 'Play'} Music
      </button>
      <Link to="/story">
        <button className="start-btn">Begin Our Love Story</button>
      </Link>
    </div>
  );
};

export default Page1;
