import React, { useState } from 'react';
import './PuzzleGame.css';

const PuzzleGame = () => {
  // Initial Sudoku puzzle data
  const initialPuzzle = [
    [1, 0, 3],
    [0, 2, 0],
    [0, 0, 1],
  ];

  // State to keep track of puzzle values
  const [grid, setGrid] = useState(initialPuzzle);

  // Handle changes to the grid cells
  const handleChange = (e, row, col) => {
    const newGrid = grid.map((rowArray, rowIndex) =>
      rowIndex === row
        ? rowArray.map((value, colIndex) =>
            colIndex === col ? parseInt(e.target.value) : value
          )
        : rowArray
    );
    setGrid(newGrid);
  };

  // Render the Sudoku grid
  const renderGrid = () => {
    return grid.map((row, rowIndex) => (
      <div className="row" key={rowIndex}>
        {row.map((cell, colIndex) => (
          <input
            key={colIndex}
            type="number"
            min="1"
            max="3"
            value={cell === 0 ? '' : cell}
            onChange={(e) => handleChange(e, rowIndex, colIndex)}
            className="cell"
          />
        ))}
      </div>
    ));
  };

  return <div className="puzzle-container">{renderGrid()}</div>;
};

export default PuzzleGame;
