import React, { useState, useEffect } from 'react';
import './PuzzleGame.css';

// Function to generate a random candy color for the game board
const generateRandomCandy = () => {
  const colors = ['#ff6666', '#66ccff', '#99cc66', '#ffcc33', '#cc66ff', '#ff9933'];
  return colors[Math.floor(Math.random() * colors.length)];
};

const PuzzleGame = ({ level }) => {
  const [gameBoard, setGameBoard] = useState([]);
  const [isGameWon, setIsGameWon] = useState(false);
  const [showHowToPlay, setShowHowToPlay] = useState(true); // New state to toggle instructions visibility

  // Initialize the board with random candies
  const initializeBoard = () => {
    const board = [];
    for (let row = 0; row < 8; row++) {
      const rowArray = [];
      for (let col = 0; col < 8; col++) {
        rowArray.push(generateRandomCandy());
      }
      board.push(rowArray);
    }
    setGameBoard(board);
  };

  // Check for matches (three or more candies in a row or column)
  const checkMatches = () => {
    let newBoard = [...gameBoard];
    let matches = [];

    // Horizontal match check
    for (let row = 0; row < 8; row++) {
      for (let col = 0; col < 6; col++) {
        if (
          newBoard[row][col] === newBoard[row][col + 1] &&
          newBoard[row][col] === newBoard[row][col + 2]
        ) {
          matches.push([row, col], [row, col + 1], [row, col + 2]);
        }
      }
    }

    // Vertical match check
    for (let col = 0; col < 8; col++) {
      for (let row = 0; row < 6; row++) {
        if (
          newBoard[row][col] === newBoard[row + 1][col] &&
          newBoard[row][col] === newBoard[row + 2][col]
        ) {
          matches.push([row, col], [row + 1, col], [row + 2, col]);
        }
      }
    }

    if (matches.length > 0) {
      // Mark matched candies as null (clearing them)
      matches.forEach(([row, col]) => {
        newBoard[row][col] = null;
      });
      setGameBoard(newBoard);
      return true;
    }
    return false;
  };

  // After matching candies, drop new ones to fill empty spots
  const dropCandies = () => {
    let newBoard = [...gameBoard];
    for (let col = 0; col < 8; col++) {
      // Fill empty spots by shifting candies down
      for (let row = 7; row >= 0; row--) {
        if (newBoard[row][col] === null) {
          for (let r = row - 1; r >= 0; r--) {
            if (newBoard[r][col] !== null) {
              newBoard[row][col] = newBoard[r][col];
              newBoard[r][col] = null;
              break;
            }
          }
        }
      }
    }
    setGameBoard(newBoard);
  };

  // Check if the game is won (e.g., if the board is filled)
  const checkWin = () => {
    const filled = gameBoard.every(row => row.every(cell => cell !== null));
    if (filled) {
      setIsGameWon(true);
    }
  };

  // Handle candy click (swap candies)
  const handleCandyClick = (row, col) => {
    let newBoard = [...gameBoard];
    if (row < 7) {
      let temp = newBoard[row][col];
      newBoard[row][col] = newBoard[row + 1][col];
      newBoard[row + 1][col] = temp;
      setGameBoard(newBoard);

      // Check if the swap resulted in matches
      if (!checkMatches()) {
        // If no match, swap back
        temp = newBoard[row][col];
        newBoard[row][col] = newBoard[row + 1][col];
        newBoard[row + 1][col] = temp;
        setGameBoard(newBoard);
      } else {
        // After matching, drop new candies
        dropCandies();
        checkWin();
      }
    }
  };

  useEffect(() => {
    initializeBoard();
  }, [level]);

  useEffect(() => {
    if (isGameWon) {
      setTimeout(() => {
        alert("Congratulations, you won this level!");
      }, 1000);
    }
  }, [isGameWon]);

  return (
    <div className="game-container">
      <h1>Level {level}</h1>

      {/* How-to-play toggle button */}
      <button className="how-to-play-btn" onClick={() => setShowHowToPlay(!showHowToPlay)}>
        {showHowToPlay ? 'Hide Instructions' : 'How to Play'}
      </button>

      {/* How-to-play instructions */}
      {showHowToPlay && (
        <div className="how-to-play">
          <h2>How to Play</h2>
          <ul>
            <li>Click on a candy to swap it with an adjacent one.</li>
            <li>Match 3 or more candies of the same color in a row or column.</li>
            <li>When you make a match, those candies will disappear, and new ones will drop down.</li>
            <li>Your goal is to fill the board with candies and win the level!</li>
          </ul>
        </div>
      )}

      <div className="scoreboard">
        <span className="score-label">Score:</span>
        <span className="score">{/* Add your score calculation logic here */}</span>
      </div>

      <div className="game-board">
        {gameBoard.map((row, rowIndex) => (
          <div key={rowIndex} className="game-row">
            {row.map((cell, colIndex) => (
              <div
                key={colIndex}
                className="game-cell"
                style={{ backgroundColor: cell }}
                onClick={() => handleCandyClick(rowIndex, colIndex)}
              />
            ))}
          </div>
        ))}
      </div>

      {isGameWon && <div className="win-message">You Won this Level!</div>}
    </div>
  );
};

export default PuzzleGame;
