import React, { useState } from 'react';

function CipherCracker({ onComplete }) {
  const [input, setInput] = useState('');
  const correctMessage = 'LOVE YOU';

  const decodeMessage = (message) => {
    return message.split('').map(char => String.fromCharCode(char.charCodeAt(0) - 1)).join('');
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (decodeMessage(input) === correctMessage) {
      onComplete();
    }
  };

  return (
    <div>
      <h2>Code Cracker</h2>
      <p>Decode the message:</p>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default CipherCracker;
